import classes from "./CheckOut.module.css";
import useInput from "../../hooks/use-input";

import FormInput from "../UI/FormInput";

const isNotEmpty = (value) => value?.trim() !== "";
const isCity = (value) => value?.trim().length > 5;

const CheckOut = (props) => {
  const {
    value: enteredName,
    valueChangeHandler: nameChangeHandler,
    blurChangeHandler: nameBlurChangeHandler,
    isvalid: firstNameIsValid,
    reset: resetName,
    hasError: nameHasError,
  } = useInput(isNotEmpty);

  const {
    value: enteredStreet,
    valueChangeHandler: streetChangeHandler,
    blurChangeHandler: streetBlurChangeHandler,
    isvalid: streetIsValid,
    reset: resetStreet,
    hasError: streetHasError,
  } = useInput(isNotEmpty);
  const {
    value: enteredPostal,
    valueChangeHandler: postalChangeHandler,
    blurChangeHandler: postalBlurChangeHandler,
    isvalid: postalIsValid,
    reset: resetPostal,
    hasError: PostalHasError,
  } = useInput(isNotEmpty);
  const {
    value: enteredCity,
    valueChangeHandler: cityChangeHandler,
    blurChangeHandler: cityBlurChangeHandler,
    isvalid: cityIsValid,
    reset: resetCity,
    hasError: cityHasError,
  } = useInput(isCity);

  let formIsValid = false;

  if (firstNameIsValid && streetIsValid && cityIsValid && postalIsValid) {
    formIsValid = true;
  }

  const confirmHandler = (event) => {
    event.preventDefault();

    if (!formIsValid) {
        return;
      }

    resetName();
    resetCity();
    resetPostal();
    resetStreet();
  };

  const nameClass = nameHasError ? "form-control invalid" : "form-control";
  const streetClass = streetHasError ? "form-control invalid" : "form-control";
  const postalClass = PostalHasError ? "form-control invalid" : "form-control";
  const cityClass = cityHasError ? "form-control invalid" : "form-control";

  return (
    <form className={classes.form} onSubmit={confirmHandler}>
        <div className={classes.control}>
      <FormInput
        label={"name"}
        labelContent={"Your Name"}
        className={nameClass}
        onChange={nameChangeHandler}
        onBlur={nameBlurChangeHandler}
        value={enteredName}
        type={"text"}
        id={"name"}
        error={nameHasError}
        errorMessage={"Please enter a  name."}
      ></FormInput>

      <FormInput
        label={"street"}
        labelContent={"Street"}
        className={streetClass}
        onChange={streetChangeHandler}
        onBlur={streetBlurChangeHandler}
        value={enteredStreet}
        type={"text"}
        id={"street"}
        error={streetHasError}
        errorMessage={"Please enter a street."}
      ></FormInput>

      <FormInput
        label={"postal"}
        labelContent={"Postal Code"}
        className={postalClass}
        onChange={postalChangeHandler}
        onBlur={postalBlurChangeHandler}
        value={enteredPostal}
        type={"text"}
        id={"postal"}
        error={PostalHasError}
        errorMessage={"Please enter a postal."}
      ></FormInput>
      <FormInput
        label={"city"}
        labelContent={"City"}
        className={cityClass}
        onChange={cityChangeHandler}
        onBlur={cityBlurChangeHandler}
        value={enteredCity}
        type={"text"}
        id={"city"}
        error={cityHasError}
        errorMessage={"Please enter a city name."}
      ></FormInput>
      <div className={classes.actions}>
        <button type="button" onClick={props.onCancel}>
          Cancel
        </button>
        <button className={classes.submit}>Confirm</button>
      </div>
      </div>
    </form>
  );
};
export default CheckOut;
