import { useReducer } from 'react';

const initialInputState ={
    value:'',
    isTouch:false,
}

const inputStateReducer = (action,state) =>{
    if(action.type==="VALUE"){
        return {value:action.value, isTouch:state.isTouch}
    }
    if(action.type==="BLUR"){
        return {value:state.value , isTouch:true }
    }
    if(action.type==="RESET"){
            return{value:'',isTouch:false}
    }
}

const useInput = (validateValue) =>{
    const [inputState, dispatch] = useReducer(
        inputStateReducer,
        initialInputState
      );

      const valueBlurHandler = () =>{
        dispatch({type:"BLUR"});
      }

      const valueChangeHandler = (event) =>{
        dispatch({type:"VALUE",value:event.target.value})
      }

      const reset = ()=>{
        dispatch({type:"RESET"});

      }

      const isValueValid = validateValue(inputState.value);
      const hasError = !isValueValid && inputState.isTouch;


      return {
        value:inputState.value,
        valueBlurHandler,
        valueChangeHandler,
        reset,
        hasError,
        isvalid:isValueValid
      }

}

export default useInput;
